  `case_steps` int(11) DEFAULT NULL COMMENT '处理措施',
  `case_steps_name` varchar(255) DEFAULT NULL COMMENT '处理措施名称',
  `case_steps_date` varchar(255) DEFAULT NULL COMMENT '处理措施变更时间',
  `case_people_id` int(255) NOT NULL COMMENT '案件人ID',
  `case_first_police_id` int(11) DEFAULT NULL COMMENT '第一负责民警',
  `case_second_police_id` int(11) DEFAULT NULL COMMENT '第二负责民警',
  `case_legal_police_id` int(11) DEFAULT NULL COMMENT '法制民警ID',
  `case_create_date` varchar(255) DEFAULT NULL COMMENT '创建(立案)日期',
  `case_create_police_id` int(11) DEFAULT NULL COMMENT '创建人',
  `case_create_police_name` varchar(255) DEFAULT NULL COMMENT '创建人',
  `case_custom` int(11) DEFAULT '0' COMMENT '是否需要自定义规则0否 1是',
  `case_complete` int(11) DEFAULT '0' COMMENT '完成',
  `case_remand` int(11) DEFAULT '0' COMMENT '是否关联取保候审信息',
  `case_supervision` int(11) DEFAULT '0' COMMENT '是否关联监视居住信息',
  `case_fugitive` int(11) DEFAULT '0' COMMENT '是否是在逃案',
  `case_explain` varchar(255) DEFAULT NULL COMMENT '行政处罚情况',
  `case_admin_hold_day` int(11) DEFAULT NULL COMMENT '行政拘留期限',
  `case_admin_money` varchar(255) DEFAULT NULL COMMENT '行政罚款金额 ',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=321 DEFAULT CHARSET=utf8;

